/*    Demonstrating ops on strings i.e. checks for equivalence etc.        Sherine M Antoun    CMU 2021 Spring    erase and append operations on strings*/#include <iostream>#include <string>using namespace std;int main(){	string s1;	cin >> s1;				// read a string from the tty	cout << s1 << endl;	string s2;	cin >> s2;				// read a string from the tty	cout << s2 << endl;	s2.append(s1, 0, 2);	// append the string	cout << s2 << endl;	s2.erase(0, s2.size());	// erase part of the string	cout << s2 << endl;	return 0;}