/*    Demonstrating ops on strings i.e. checks for equivalence etc.        Sherine M Antoun    CMU 2021 Spring*/#include <iostream>#include <string>using namespace std;int main(){	string name1;	string name2;	cout << "Enter name1: ";	cin>> name1;	cout << "Enter name2:";	cin >> name2;	if (name1 == name2)			// equivelence	{		cout << "Go home"<<endl;	}	else	{		string s3 = name1 + name2;	// concatenation		cout << s3 << endl;		for (int i = 0; i < s3.size(); i++)		// array subscripts		{			cout << s3[i] << endl;		}	}	return 0;}